        //         (e) => setValue(e.target.value)
        //         // console.log(e.target.value)
        //     } 
        //     />
        <>
        <input type="checkbox">{task.task}</input>        

        <button type="submit" className="todo-btn"><FontAwesomeIcon icon={faPlus} style={{color: "#ffffff"}} />Update Task</button>     
        </>
    )
}
