import React, {useState, useEffect} from 'react'
import { v4 as uuidv4 } from 'uuid'
import { Todo } from './Todo'
import { TodoForm } from './TodoForm'
import { EditTodoForm } from './EditTodoForm'
import { TaskModal } from '../TaskModal'
// uuidv4();

export const TodoWrapper = () => {
                const [todos, setTodos] = useState([])
                const addTodo = todo => {
                        setTodos([...todos, {id: todos.length+1, task: todo, completed: false, isEditing: false}])
                        // console.log(todos)

                        if(todos.length > 7) {
                                setTodos([...todos])
                        }
                }
// uuidv4()     

                // useEffect(() => {
                //         console.log(todos); // 상태 업데이트 이후에 todos 상태를 로깅
                // }, [todos]); 

                useEffect(() => {
                        // Fetch todos based on selectedCategoryId
                        const fetchedTodos = fetchTodos(selectedCategoryId);
                        setTodos(fetchedTodos);
                }, [selectedCategoryId]);

                const toggleComplete = id => {
                        setTodos(todos.map(todo => todo.id === id ? {...todo, completed: !todo.completed} : todo))
                        console.log("Completed!")
                }
                const editTodo = id => {
                        setTodos(todos.map(todo => todo.id === id ? {...todo, isEditing: !todo.isEditing} : todo))
                }
                const deleteTodo = id => {
                        setTodos(todos.filter(todo => todo.id !== id))
                }

                const editTask = (task, id) => {
                        setTodos(todos.map(todo => todo.id === id ? {...todo, task, isEditing: !todo.isEditing} : todo))
                }
        return (
                <div className= "TodoWrapper">                 
                <h3 className="title">To Do List</h3>     
                        <TodoForm addTodo={addTodo} /> 
                        {todos.map((todo, index) => (
                                todo.isEditing ? (
                                        <EditTodoForm editTodo={editTask} task={todo} />
                                ) : (
                                <Todo task={todo} key={index} toggleComplete={toggleComplete} deleteTodo={deleteTodo} editTodo={editTodo}/>                          
                                )
                        ))}  
                </div>
        )
}
