import React from 'react';
import { Component } from 'react';
// import Modal from 'react-modal';
import User from './User';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faUser, faArrowRightFromBracket } from '@fortawesome/free-solid-svg-icons';

class UserModal extends Component {
    state = {
            file: null,
            // profilePicture: <FontAwesomeIcon icon={faUser} style={{color: "#ffffff",}} className="user-icon"/>,
            name: "",
            previewURL: ''

    };

    loginHandler = (e) => {
        const { name, value } = e.target;
        this.setState({ [name] : value });
    };


    imageSelectedHandler = (event) => {
        // console.log(event.target.files[0]);           
            // selectedFile: event.target.files[0]
    }


    fileUploadHandler = (event) => {
        // event.preventDefault();
        const reader = new FileReader();
        let file = event.target.files[0]; 

        reader.onloadend = () => {
            this.setState({
                file : file,
                previewURL : reader.result
            })
        }    
            console.log(file);  
        // reader.readAsDataURL(file);

        // console.log(event.target.files[0]);           
        // selectedFile: event.target.files[0];
        // axios.post('');
    }

    render() {
            const { isOpen, close } = this.props;
            let profile_priview = null;
            if(this.state.file !== '' ) { 
                const profile_preview = <img className="profile_priview" src={this.state.previewURL} />
            }

            return (
                <>
                    { 
                        isOpen == true
                        ?  
                        <div className="modalBackground">
                            <button type="button" className="close" onClick={close}>x</button>
                            <div className="modalContainer">
                                <button onClick={this.fileUploadHandler} className="User"><FontAwesomeIcon icon={faUser} style={{color: "#ffffff",}} className="user-icon"/></button>
                                {/* {profile_preview} */}
                                <input type="file" accept = "image/jpg,image/png,image/jpeg" name="profile_img" onChange={this.fileUploadHandler} onClick={this.fileUploadHandler} className="profile"></input> 
                                <p className="UserName">User<button type="button" className="edit-btn"><FontAwesomeIcon icon={faPen} /></button></p>
                                <div className="Btns">
                                    <button type="button" className="change-btn" >Change Name</button>
                                    <button type="button" className="save-btn">Save Name</button> 
                                    <button type="button" className="logout-btn">Logout <FontAwesomeIcon icon={faArrowRightFromBracket} /></button>    
                                </div>
                            </div>
                        </div>
                        : null
                    }
                </>
            )
    }        
}

export default UserModal
