export const TaskModal = () => {
    const [dueDate, setDueDate] = useState(new Date());   
    const [note, setNote] = useState("")
    const [image, setImage] = useState(null);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };
