import React, {useState} from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faTrashCan, faLink, faStar } from '@fortawesome/free-solid-svg-icons';
import { EditTaskModal } from '../Task/EditTaskModal'
import { TaskModal } from '../Task/TaskModal'
import { TodoWrapper } from './TodoWrapper';

export const Todo = ({task, toggleComplete, deleteTodo, searchTerm, toggleImportance}) => {
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isTodoOpen, setIsTodoOpen] = useState(false);

    const openEditModal = () => {
        setIsEditModalOpen(true);
    };

    const closeEditModal = () => {
        setIsEditModalOpen(false);
    };

    const handleEditIconClick = () => {
        openEditModal(); 
    };

    const handleToggleImportance = async (id) => {
        try {
            const task = todos.find(todo => todo.id === id)
            const updatedImportantStatus = !task.isImportant; // 현재 상태를 반전시킴
            await axios.patch(`http://localhost:3001/tasks/${task.id}`, { isImportant: updatedImportantStatus });
            toggleImportance(task.id, updatedImportantStatus); // 클라이언트 상태 업데이트
        } catch (error) {
            console.error('Error toggling importance:', error);
        }
    };    

    const highlightSearchTerm = (text, searchTerm) => {
        if (!searchTerm.trim()) {
            return text;
        }
        const regex = new RegExp(`(${searchTerm})`, 'gi');
        return text.replace(regex, (match) => `<span class="highlight">${match}</span>`);
    }

    const toggleTodoOpen = () => {
        setIsTodoOpen(!isTodoOpen);
    }

    return (
        <>
            <div className="Todo">
                    <div className="checkContainer" onClick={toggleTodoOpen}>
                        <div>
                            <label className="checkbox-label">
                                <input id="checkbox" type="checkbox" onClick={(e) => { e.stopPropagation(); toggleComplete(task.id)}} className={`${task.isCompleted ? 'isCompleted' : ''}`} checked={task.isCompleted} />
                                <span class="checkmark"></span>                        
                                <p id="taskName" className={`${task.isCompleted ? 'isCompleted' : ""}`} dangerouslySetInnerHTML={{ __html: highlightSearchTerm(task.task, searchTerm) }} />
                            </label>                            
                        </div>

                        {/* className={`${task.completed ? 'complete' : ''}`}  */}
                        {/* <p className={`${task.completed ? 'completed' : ""}`}>{task.task}</p>  */}

                        <div className="button-container">                             

                            <button type="button" className={task.isImportant ? 'important' : ''} onClick={() => handleToggleImportance(task.id)}>

                                <FontAwesomeIcon icon={faStar} />
                            </button>
                            <p className="todo-category">{task.category}</p>                            
                            <button type="button">
                                <FontAwesomeIcon icon={faPen} onClick={handleEditIconClick} />
                            </button>
                            <button type="button">
                                <FontAwesomeIcon icon={faTrashCan} onClick={() => deleteTodo(task.id)} />
                            </button>   
                        </div>
                    </div>        
                    <div className={`detailContainer ${isTodoOpen ? 'open' : ''}`}>
                        <p className="todo-note">{task.note}</p>            
                        <Link to={task.url}><FontAwesomeIcon icon={faLink} className="link-icon" />{task.url}</Link>                             


                    </div>
            </div>
            <EditTaskModal isOpen={isEditModalOpen} openModal={openEditModal} close={closeEditModal} task={task}/>            
        </>
    )
}
