import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faPaperPlane } from '@fortawesome/free-solid-svg-icons'
import { Todo } from './Todo'

export const EditTodoForm = ({editTodo, task}) => {
    const [value, setValue] = useState(task.task)
    const handleSubmit = (e) => {
        e.preventDefault();

        // console.log(value)
        editTodo(value, task.id);

        setValue("")
    }
    return (

        <form className="TodoForm" onSubmit={handleSubmit}>
            <input type="checkbox" onClick={() => toggleComplete(task.id)} className={`${task.completed ? 'completed' : ""}`} /><input type="text" className="task-input" value={value} placeholder="Update Task" required autoFocus maxLength="25" style={{ background: 'none', border: 'none', height: '30px'}} onChange={
                (e) => setValue(e.target.value)
                // console.log(e.target.value)
            } 
            />
        <button type="submit" className="todo-btn"><FontAwesomeIcon icon={faPaperPlane} style={{color: "#ffffff"}} /></button>           
        </form>
    )
}
