import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons'

const Todo = ({task, toggleComplete, deleteTodo, editTodoForm}) => {
    return (
        <div className="Todo">
                <input type="checkbox" onClick={() => toggleComplete(task.id)} className={`${task.completed ? 'completed' : ""}`} />
                <p className={`${task.completed ? 'completed' : ""}`}>{task.task}</p> 
                <div>
                    {/* <button type="button"> */}
                        <FontAwesomeIcon icon={faPen} onClick={() => editTodoForm(task.id)}/>
                        {/* // </button> */}
                    {/* // <button type="button"> */}
                        <FontAwesomeIcon icon={faTrashCan} onClick={() => deleteTodo(task.id)} />
                        {/* // </button>                            */}
                </div>                    
        </div>
    )
}
export default Todo
