import React from 'react';
import { Component } from 'react';
// import Modal from 'react-modal';
import User from './User';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faUser, faArrowRightFromBracket } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

class UserModal extends Component {
    state = {
            // profilePicture: <FontAwesomeIcon icon={faUser} style={{color: "#ffffff",}} className="user-icon"/>,
            userImage: "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png",
            selectedFile: null,
            name: "",

    };

    loginHandler = (e) => {
        const { name, value } = e.target;
        this.setState({ [name] : value });
    };


    imageSelectedHandler = (e) => {
        //console.log(e.target.files[0])
        this.setstate({
        selectedFile: e.target.files[0]
        })
    }

    imageUploadHandler = (e) => {
        // const formData = new FormData();
        // axios.post('');
        e.preventDefault();
        const reader = new FileReader();
        const file = e.target.files[0];
        console.log(e.target.files[0])
        reader.onloadend = () => {
            this.setState({
                file: file,
                userImage: reader.result
            });
        }
        reader.readAsDataURL(file)
    }
    render() {
            const { isOpen, close } = this.props;
            const { userImage } = this.state;

            return (
                <>
                    { 
                        isOpen == true
                        ?  
                        <div className="modalBackground">
                            <button type="button" className="close" onClick={close}>x</button>
                            <div className="modalContainer">
                                <button onClick={this.imageSelectedHandler} className="User"><FontAwesomeIcon icon={faUser} style={{color: "#ffffff",}} className="user-icon"/></button>
                                <input type="file" accept = "image/*" name="profile_img" onClick={this.imageSelectedHandler} className="profile"></input> 
                                <p className="UserName">User<button type="button" className="edit-btn"><FontAwesomeIcon icon={faPen} /></button></p>
                                <div className="Btns">
                                    <button type="button" className="change-btn" >Change Name</button>
                                    <button type="button" className="save-btn">Save Name</button> 
                                    <button type="button" className="logout-btn">Logout <FontAwesomeIcon icon={faArrowRightFromBracket} /></button>    
                                </div>
                            </div>
                        </div>
                        : null
                    }
                </>
            )
    }        
}

export default UserModal
