import React, {useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons'

export const Category = ({addCategory}) => {
    const [category, setCategory] = useState("")
    const handleSubmit = (e) => {
        e.preventDefault();

        // console.log(value)
        addCategory(category);

        setCategory("")
    }
    return (
        <div>
            <h3 className="title">Category</h3>  
            <form className="CategoryForm" onSubmit={handleSubmit}>
                <input type="text" className="category-input" value={CATEGORY} placeholder="Create A New Category" required autoFocus maxLength="25" onChange={
                    (e) => setValue(e.target.value)
                    // console.log(e.target.value)
                } 
                />
                <button type="submit" className="category-btn"><FontAwesomeIcon icon={faPlus} style={{color: "#ffffff",}} /></button>           
            </form> 
        </div>
    )
}

