import React, { useState } from 'react'
import { TodoForm } from './TodoForm'
import { v4 as uuidv4 } from 'uuid'
import Category from './Category'
import { CategoryForm } from './CategoryForm'
import { EditCategoryForm } from './EditCategoryForm'

uuidv4();

export const CategoryWrapper = () => {
        const [categories, setCategories] = useState([])

        const addCategory = category => {
                setCategories([...categories, {id: uuidv4(), title: categories, isEditing: false}])
                console.log(categories)
        }

        const editCategory = id => {
                setCategories(categories.map(category => category.id === id ? {...category, isEditing: !category.isEditing} : category))
        }
        const deleteCategory = id => {
                setCategories(categories.filter(category => category.id !== id))
        }
        return (
                <div className="CategoryWrapper">                 
                <h3 className="title">Category Name</h3>     
                <CategoryForm addCategory={addCategory} />  
                        {categories.map((category, index) => (
                                category.isEditing ? (
                                        <EditCategoryForm editTodo={editCategory} task={category} />
                                ) : (
                                <Category task={category} key={index} deleteCategory={deleteCategory} editCategory={editCategory}/>                                        
                                )
                        ))}  
                        {/* <CategoryForm /> */}
                </div>
        )
}
