import React, {useState} from 'react';
import { Component } from 'react';
import Modal from 'react-modal';
import User from './User';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faArrowRightFromBracket } from '@fortawesome/free-solid-svg-icons';

class UserModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isModalOpen: false,
            profilePicture: "",
            name: "",
        };
    }
        loginHandler = (e) => {
            const { name, value } = e.target;
            this.setState({ [name] : value });
        };

        closeModal = () => {
            this.setState({ isModalOpen: false});
        }

        imageLoader = (e) => {

        }


    render() {
            const { isOpen, close } = this.props;
            return (
                <>
                    { 
                        isOpen == true
                        ?  
                        <div className="modalBackground">
                            <button type="button" className="close-btn" onClick={this.closeModal}>x</button>
                            <div className="modalContainer" onClick={isOpen}>
                                <User className="User"></User> <User />
                                <input type="file" accept = "image/*"/>
                                <p className="UserName">User<button type="button" className="edit-btn"><FontAwesomeIcon icon={faPen} /></button></p>
                                <div className="Btns">
                                    <button type="button" className="change-btn" >Change Name</button>
                                    <button type="button" className="save-btn">Save Name</button> 
                                    <button type="button" className="logout-btn">Logout <FontAwesomeIcon icon={faArrowRightFromBracket} /></button>    
                                </div>
                            </div>
                        </div>
                        : null
                    }
                </>
            )
    }        
}

export default UserModal
