import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Todo } from './Todo';
import { TodoForm } from './TodoForm';
import { TaskModal } from '../Task/TaskModal'; 

export const TodoWrapper = () => {
    const [todos, setTodos] = useState([]);
    const [filteredTasks, setFilteredTasks] = useState([])
    const [selectedCategory, setSelectedCategory] = useState('');
    const fetchTasks = async () => {
        try {
            const updatedTasks = await axios.get('http://localhost:3001/tasks');
            setTodos(updatedTasks.data);
            setFilteredTasks(updatedTasks.data, selectedCategory);
        } catch (error) {
            console.error('Error fetching tasks:', error);
        }
    };

    useEffect(() => {
        fetchTasks();
    }, [todos]); 

    const addTodo = async todo => {
        try {
            const response = await axios.post('http://localhost:3001/tasks', {
                task: todo,
                category: 'all',
                completed: false,
                isEditing: false
            });
            const newTodos = [...todos, response.data];
            // setTodos([...todos, response.data]);
            setTodos(newTodos)
            filterTasks(newTodos, selectedCategory)
            console.log('New task added:', response.data);
        } catch (error) {
            console.error('Error adding task:', error);
        }
    };

    const deleteTodo = async id => {
        try {
            await axios.delete(`http://localhost:3001/tasks/${id}`);
            const updatedTodos = todos.filter(todo => todo.id !== id);
            setTodos(updatedTodos);
            filterTasks(updateTodos, selectedCategory);
            console.log('Task deleted:', id);
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const toggleComplete = async id => {
        try {
            const updatedTodo = todos.find(todo => todo.id === id);
            const updatedData = { completed: !updatedTodo.completed };
            await axios.patch(`http://localhost:3001/tasks/${id}`, updatedData);
            const updatedTodos = todos.map(todo =>
                todo.id === id ? { ...todo, completed: !todo.completed } : todo
            );
            setTodos(updatedTodos);
            filterTasks(updatedTodos, selectedCategory);
            console.log("Task complete status updated:", id);
        } catch (error) {
            console.error('Error updating task complete status:', error);
        }
    };

    const editTodo = async (id, updatedTask) => {
        try {
            const updatedTodo = todos.find(todo => todo.id === id);
            const updatedData = { task: updatedTask, isEditing: false };
            await axios.patch(`http://localhost:3001/tasks/${id}`, updatedData);
            const updatedTodos = todos.map(todo =>
                todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
            );
            setTodos(updatedTodos);
            filterTasks(updatedTodos, selectedCategory);
            console.log("Task is updated:", id);
        } catch (error) {
            console.error('Error updating task', error);
        }
    };

    const filterTasks = (category) => {
        if(category === 'all') {
            setFilteredTasks(todos);
        } else {
            const filtered = todos.filter(todo => todo.category === category);
            setFilteredTasks(filtered)
        }
    }
// addTodo={addTodo} 
    return (
        <div className="TodoWrapper">
            <h3 className="title">To Do List</h3>
            <TodoForm filterTasks={filterTasks}/>
            {filteredTasks.map((task) => (
                <Todo
                    task={task}
                    key={task.id}
                    toggleComplete={toggleComplete}
                    deleteTodo={deleteTodo}
                    editTodo={editTodo}
                />
            ))}
        </div>
    );
};
