import React, {useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons'

export const Todo = ({task, toggleComplete, deleteTodo, editTodo}) => {
    const {previewURL} = useState("");
    const fileUploadHandler = (event) => {
        // event.preventDefault();
        const reader = new FileReader();
        const file = event.target.files[0]; 

        // 파일이 이미지인지 확인
        if (file) {
            reader.onloadend = () => {
                this.setState({
                    file : file,
                    previewURL : reader.result
                });

                this.props.handleImageUpload(reader.result);
            }    
            reader.readAsDataURL(file);
        }
    };


    return (
        <div className="Todo">
                <input type="checkbox" onClick={() => toggleComplete(task.id)} className={`${task.completed ? 'completed' : ""}`} />
                <p className={`${task.completed ? 'completed' : ""}`}>{task.task}</p> 
                <div>
                    <button type="button">
                        <FontAwesomeIcon icon={faPen} onClick={() => editTodo(task.id)}/>
                    </button>
                    <button type="button">
                        <FontAwesomeIcon icon={faTrashCan} onClick={() => deleteTodo(task.id)} />
                    </button>                           
                </div>                    
        </div>
    )
}
