export const Search = ({ handleSearch }) => {
    const [searchTerm, setSearchTerm] = useState('')

    const handleChange = (e) => {
        setSearchTerm(e.target.value);
        handleSearch(e.target.value);
        console.log(e.target.value)
    }
        return (
            <div className="Search">

                <input type="search" className="search-input" placeholder="Search..." value={searchTerm} onChange={handleChange}><FontAwesomeIcon icon={faMagnifyingGlass} style={{color: "#a8a8a8",}} className="search-icon" />   </input>
            </div>
        )
    }

