import React, {useState} from 'react'

export const TodoForm = ({addTodo}) => {
    const [value, setValue] = useState("")
    const handleSubmit = (e) => {
        e.preventDefault();

        // console.log(value)
        addTodo(value);

        setValue("")
    }
    return (
        <form className="TodoForm" onSubmit={handleSubmit}>
            <input type="text" className="task-input" value={value} placeholder="What is your task today?" required autofocus maxLength="15" onChange={
                (e) => setValue(e.target.value)
                // console.log(e.target.value)
            } 
            />
        <button type="submit" className="todo-btn"><FontAwesomeIcon icon={faPlus} style={{color: "#7e6de5",}} /></button>           
        </form>

    )
}
