import React, {useState} from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons';
import { EditTaskModal } from '../Task/EditTaskModal'
import { TaskModal } from '../Task/TaskModal'

export const Todo = ({task, toggleComplete, deleteTodo, searchTerm}) => {
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);

    const openEditModal = () => {
        setIsEditModalOpen(true);
    };

    const closeEditModal = () => {
        setIsEditModalOpen(false);
    };

    const highlightSearchTerm = (text, searchTerm) => {
        if (!searchTerm.trim()) {
            return text;
        }
        const regex = new RegExp(`(${searchTerm})`, 'gi');
        return text.replace(regex, (match) => `<span class="highlight">${match}</span>`);
    }

    return (
        <>
            <div className="Todo">
                    <input type="checkbox" onClick={() => toggleComplete(task.id)} className={`${task.completed ? 'completed' : ""}`} />
                    {/* <p className={`${task.completed ? 'completed' : ""}`}>{task.task}</p>  */}
                    <p className={`${task.completed ? 'completed' : ""}`} dangerouslySetInnerHTML={{ __html: highlightSearchTerm(task.task, searchTerm) }} />
                    <p className="tasklist_category">{task.category}</p>
                    <p className="note">{task.note}</p>
                    <div>
                        <button type="button">
                            <FontAwesomeIcon icon={faPen} onClick={openEditModal}/>
                        </button>
                        <button type="button">
                            <FontAwesomeIcon icon={faTrashCan} onClick={() => deleteTodo(task.id)} />
                        </button>                           
                    </div>


            </div>
            <EditTaskModal isOpen={isEditModalOpen} openModal={openEditModal} close={closeEditModal} task={task}/>            
        </>
    )
}
